#include "afx.h"
using namespace std;

class Solution
{
public:
    int numEnclaves(vector<vector<int>> &grid)
    {
        int result = 0;
        int m = grid.size();
        int n = grid[0].size();

        vector<vector<int>> history(m, vector<int>(n, -1));

        for (int i = 0; i < m; i++)
        {
            walk(grid, i, 0, m, n, history);
            walk(grid, i, n - 1, m, n, history);
        }
        for (int j = 1; j < n - 1; j++)
        {
            walk(grid, 0, j, m, n, history);
            walk(grid, m - 1, j, m, n, history);
        }

        for (int i = 1; i < m - 1; i++)
        {
            for (int j = 1; j < n - 1; j++)
            {
                if (grid[i][j] == 1 && history[i][j] == -1)
                {
                    result++;
                }
            }
        }

        return result;
    }

private:
    void walk(vector<vector<int>> &grid, int i, int j, int m, int n, vector<vector<int>> &history)
    {
        if (grid[i][j] == 0)
        {
            return;
        }

        history[i][j] = 1;
        for (int k = 0; k < 4; k++)
        {
            int nx = i + directions[k][0];
            int ny = j + directions[k][1];
            if (nx < 0 || nx >= m || ny < 0 || ny >= n || grid[nx][ny] == 0)
            {
                continue;
            }

            if (history[nx][ny] == 1)
            {
                continue;
            }

            walk(grid, nx, ny, m, n, history);
        }
    }
    static constexpr int directions[4][2] = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
};

